import React, { useState } from "react";
import Jumbotron from "./components/Jumbotron";
import Nav from "./components/Nav";
import Input from "./components/Input";
import Button from "./components/Button";
import API from "./utils/API";
import { RecipeList, RecipeListItem } from "./components/RecipeList";
import { Container, Row, Col } from "./components/Grid";

function App() {
  const [recipes, setRecipes] = useState([]);
  const [recipeSearch, setRecipeSearch] = useState("");
  const handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { value } = event.target;
    setRecipeSearch(value);
  };
  const handleFormSubmit = event => {
    // When the form is submitted, prevent its default behavior, get recipes update the recipes state
    event.preventDefault();
    API.getRecipes(recipeSearch).then(res => {
      console.log(res.data)
      setRecipes(res.data)
    }).catch(err => console.log(err));
  };
  return ( < div > < Nav / > < Jumbotron / > < Container > < Row > < Col size = "md-12" > < form > < Container > < Row > < Col size = "xs-9 sm-10" > < Input name = "recipeSearch"
      value = { recipeSearch }
      onChange = { handleInputChange }
      placeholder = "Search For a Recipe" / > < /Col> < Col size = "xs-3 sm-2" > < Button onClick = { handleFormSubmit }
      type = "success"
      className = "input-lg" > Search < /Button> < /Col> < /Row> < /Container> < /form> < /Col> < /Row> < Row > < Col size = "xs-12" > < RecipeList children = {
        recipes.map(recipie => < RecipeListItem recipie = { recipe }
          />)}/ > < /Col> < /Row> < /Container> < /div>);
      }
      export default App;
